    def write_label1(self, number):
        if number == "AC":
            self.label1.setText("0")
            self.label2.setText("0")
        elif self.label1.text() == "0" or self.label1.text() == "+" or self.label1.text() == "-" or self.label1.text() == "*" or self.label1.text() == "/":
            self.label1.setText(number)
        elif number == "+" or number == "-" or number == "*" or number == "/":
            self.first_number = self.label1.text()
            self.action = number
            self.label1.setText(number)
            self.label2.setText(self.first_number + number)
        else:
            self.label1.setText(self.label1.text() + number)

    def result(self):
        self.second_number = self.label1.text()
        string = str(self.first_number + self.action + self.second_number)
        result = eval(string)
        self.label2.setText("Result: " + str(result))
        self.label1.setText("0")

    def action2(self, action):
        if action == "x²":
            self.label2.setText("Result: " + str(float(self.label1.text())**2))
            self.label1.setText("0")
        elif action == "+/-":
            if float(self.label1.text()) > 0:
                self.label1.setText(str(-(float(self.label1.text()))))
            else:
                self.label1.setText(self.label1.text()[1::])
        elif action == "%":
            self.label2.setText("Result: " + str(float(self.label1.text()) / 100))
            self.label1.setText("0")

